import React, { useEffect, useState ,useRef } from 'react'
import { collection, getDocs,deleteDoc,doc } from "firebase/firestore";
import "./list.css"
import { db } from '../firebase';
import { Link } from 'react-router-dom';
import { UserAuth } from '../context/AuthContext';
import emailjs from "emailjs-com"
import { sendEmailVerification } from 'firebase/auth';

const List = () => {

    const[data,setData]=useState([])
    const { user } = UserAuth();
    const form=useRef();
    console.log(user.email)

    useEffect(() => {
        const fetchdata = async()=>{
            let list=[]
           try{
                const querySnapshot = await getDocs(collection(db, "users"));
                querySnapshot.forEach((doc) => {
                    console.log(doc)
                    list.push({id:doc.id,...doc.data()})
                });
                setData(list)
           }catch(e){console.log(e)}
        }
        fetchdata()
    }, []);

    const sendEmail=()=>{
        emailjs.send("service_ozmmcvk","template_knsq4cg",{
            to_name: user.email,
            to_email: user.email,
        },'U-luQdKfr0dHJGms3');
    }

    return (
        <div className='mai'>
            <form ref={form} to_name={user.email} to_email={user.email}></form>
            <ul>
                {data.map((x)=>{
                   if(x.email===user.email)
                   {
                        return (<li className='wrapper '>
                        <img className='image' src={x.img}/>
                        <p className='p'>{x.text}</p>
                        <button id={x.id} onClick={async ()=>{
                            try{
                                sendEmail()
                                await deleteDoc(doc(db,"users",x.id));
                                setData(data.filter((item)=>item.id!=x.id))
                            }catch(e){console.log(e)}
                        }} className='but'>Delete</button>
                    </li>)
                   }
                })}
            </ul>
            <div>
                    <Link to='/account' className='underline'>
                    ADD a todo
                </Link>
            </div>
        </div>
    )
}

export default List
