import React from 'react';
import { useNavigate } from 'react-router-dom';
import { UserAuth } from '../context/AuthContext';

const Account = () => {
  const { user, logout } = UserAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/');
      console.log('You are logged out')
    } catch (e) {
      console.log(e.message);
    }
  };

  return (
    <div className='max-w-[600px] mx-auto my-16 p-4'>
      <h1 className='text-2xl font-bold py-4'>Todo</h1>
      <p>User Email: {user && user.email}</p>
      <form>
        <div className='flex flex-col py-2'>
          <label className='py-2 font-medium'>Task</label>
          <input className='border p-3' type='text' placeholder="todo" />
        </div>
        <div className='flex flex-col py-2'>
          <label className='py-2 font-medium'>Image</label>
          <input className='border p-3' type='file' placeholder="Image" />
        </div>
      </form>

      <button onClick={handleLogout} className='border px-6 py-2 my-4'>
        Logout
      </button>
      <button onClick={handleLogout} className='border px-6 py-2 my-4 mx-4'>
        Submit
      </button>
    </div>
  );
};

export default Account;
