      await setDoc(doc(db,"users",user.uid),
      {
        ...data,
        timestamp:serverTimestamp()
      })
    }catch(e){console.log(e)}

  }

  return (
    <div className='max-w-[600px] mx-auto my-16 p-4'>
      <h1 className='text-2xl font-bold py-4'>Todo</h1>
      <p>User Email: {user && user.email}</p>
      <form>
        <div className='flex flex-col py-2'>
          <label className='py-2 font-medium'>Task</label>
          <input className='border p-3' type='text' placeholder="todo" id="text" onChange={handleInput} />
        </div>
        <div className='flex flex-col py-2'>
          <label className='py-2 font-medium'>Image</label>
          <input className='border p-3' type='file' placeholder="Image" id="image" onChange={handleInput} />
        </div>
      </form>

      <button onClick={handleSubmit} className='border px-6 py-2 my-4'>
        Submit
      </button>
      <button onClick={handleLogout} className='border px-6 py-2 my-4 mx-4'>
        Logout
      </button>
    </div>
  );
};

export default Account;
