import React, { useEffect, useState } from 'react'
import { collection, getDocs,deleteDoc,doc } from "firebase/firestore";
import "./list.css"
import { db } from '../firebase';
import { Link } from 'react-router-dom';
import { UserAuth } from '../context/AuthContext';

const sgmail=require('@sendgrid/mail')
sgmail.setApiKey('nre3lcYdRsyNIJ7auhW-Zw')

const List = () => {

    const[data,setData]=useState([])
    const { user } = UserAuth();
    console.log(user.email)

    useEffect(() => {
        const fetchdata = async()=>{
            let list=[]
           try{
                const querySnapshot = await getDocs(collection(db, "users"));
                querySnapshot.forEach((doc) => {
                    console.log(doc)
                    list.push({id:doc.id,...doc.data()})
                });
                setData(list)
           }catch(e){console.log(e)}
        }
        fetchdata()
    }, []);

    console.log(data)

    return (
        <div className='mai'>
            <ul>
                {data.map((x)=>{
                   if(x.email===user.email)
                   {
                    console.log(x.email)
                    console.log(user.email)
                        return (<li className='wrapper '>
                        <img className='image' src={x.img}/>
                        <p className='p'>{x.text}</p>
                        <button id={x.id} onClick={
                            async ()=>{
                            try{
                                const message ={
                                    to:user.email,
                                    from:'mdarshkhan9898@gmail.com',
                                    subject:<p>Task completed</p>
                                }
                                sgmail.send(message)
                                await deleteDoc(doc(db,"users",x.id));
                                setData(data.filter((item)=>item.id!=x.id))
                            }catch(e){console.log(e)}
                        }} className='but'>Delete</button>
                    </li>)
                   }
                })}
            </ul>
            <div>
                    <Link to='/account' className='underline'>
                    ADD a todo
                </Link>
            </div>
        </div>
    )
}

export default List
